                <div>
                  <label className="text-sm font-medium mb-2 block">Mission Description</label>
                  <Textarea
                    placeholder="Mission description..."
                    value={missionDesc}
                    onChange={(e) => setMissionDesc(e.target.value)}
                    className="min-h-32"
                    data-testid="textarea-mission-description"
                  />
